require 'java'

class RubyPrimitiveAdder
	include org.springframework.scripting.jruby.PrimitiveAdder

	def addInts(x, y)
		x + y
	end

	def addShorts(x, y)
		x + y
	end

	def addLongs(x, y)
		x + y
	end

	def addFloats(x, y)
		x + y
	end

	def addDoubles(x, y)
		x + y
	end

	def resultIsPositive(x, y)
		(x + y) > 0
	end

	def concatenate(c, d)
		return ("" << c) << d;
	end

	def echo(c)
		c
	end

end
