require 'java'

class RubyWrapperAdder
	include org.springframework.scripting.jruby.WrapperAdder

	def addInts(x, y)
		x + y
	end

	def addShorts(x, y)
		x + y
	end

	def addLongs(x, y)
		x + y
	end

	def addFloats(x, y)
		x + y
	end

	def addDoubles(x, y)
		x + y
	end

	def resultIsPositive(x, y)
		(x + y) > 0
	end

	def concatenate(c, d)
		return ("" << c) << d;
	end

	def echo(c)
		c
	end

	def concatArrayOfIntegerWrappers(numbers)
		buffer = ""
        numbers.each do |number|
        	buffer << number.to_s
        end
        return buffer
	end

	def populate(x, y)
		[x, y]
	end

	def createListOfLists(x, y, z)
		[[x], [y], [z]]
	end

	def toMap(key, value)
		{key => value}
	end

end
