package com.jiagouedu.services.manage.catalog;import com.jiagouedu.core.Services;import com.jiagouedu.services.manage.catalog.bean.Catalog;import java.util.List;public interface CatalogService extends Services<Catalog> {	/**	 * 加载指定节点下的全部节点。 如果指定的节点为NULL则加载整棵树，否则加载指定对象下的所有节点	 * 	 * @param e	 * @return	 */	public List<Catalog> loadRoot(Catalog e);	/**	 * 根据ID删除指定的目录,如果该类目下面有子类目,则会一并删除;如果该类目下面有商品,则会一并删除	 * @param id	 * @return	 */	public boolean deleteByID(String id);}