package com.jiagouedu.services.manage.task.bean;import java.io.Serializable;public class Task extends com.jiagouedu.services.common.Task implements		Serializable {	private static final long serialVersionUID = 1L;	private String id;	private String code;	private String name;	private String sleep;	private String unit;	private String nextWorkTime;	private String currentStatus;	private Thread thread;	public void clear() {		super.clear();		id = null;		code = null;		name = null;		sleep = null;		unit = null;		nextWorkTime = null;		currentStatus = null;		if (thread != null) {			thread = null;		}	}		/**	 * 获取任务的详细描述	 * @return	 */	public String getDetail(){		return null;	}		public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getSleep() {		return sleep;	}	public void setSleep(String sleep) {		this.sleep = sleep;	}	public String getUnit() {		return unit;	}	public void setUnit(String unit) {		this.unit = unit;	}	public String getNextWorkTime() {		return nextWorkTime;	}	public void setNextWorkTime(String nextWorkTime) {		this.nextWorkTime = nextWorkTime;	}	public String getCurrentStatus() {		return currentStatus;	}	public void setCurrentStatus(String currentStatus) {		this.currentStatus = currentStatus;	}	public Thread getThread() {		return thread;	}	public void setThread(Thread thread) {		this.thread = thread;	}}