package com.jiagouedu.services.manage.account.impl;import com.jiagouedu.core.ServersManager;import com.jiagouedu.services.manage.account.AccountService;import com.jiagouedu.services.manage.account.bean.Account;import com.jiagouedu.services.manage.account.dao.AccountDao;import org.apache.commons.lang.StringUtils;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("accountServiceManage")public class AccountServiceImpl extends ServersManager<Account, AccountDao> implements        AccountService {    @Resource(name="accountDaoManage")    @Override    public void setDao(AccountDao accountDao) {        this.dao = accountDao;    }	/**	 * 批量删除	 * 	 * @param ids	 * @return	 */	public int deletes(String[] ids) {		if (ids == null || ids.length == 0) {			throw new NullPointerException("id不能全为空！");		}				for (int i = 0; i < ids.length; i++) {			if(StringUtils.isBlank(ids[i])){				throw new NullPointerException("id不能为空！");			}			dao.deleteById(Integer.parseInt(ids[i]));		}		return 0;	}	}