package com.jiagouedu.services.manage.comment.impl;import com.jiagouedu.core.ServersManager;import com.jiagouedu.services.manage.comment.CommentService;import com.jiagouedu.services.manage.comment.bean.Comment;import com.jiagouedu.services.manage.comment.dao.CommentDao;import org.apache.commons.lang.StringUtils;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("commentServiceManage")public class CommentServiceImpl extends ServersManager<Comment, CommentDao> implements        CommentService {    @Resource(name = "commentDaoManage")    @Override    public void setDao(CommentDao commentDao) {        this.dao = commentDao;    }	@Override	public void updateStatus(String[] ids, String status) {		if (ids == null || ids.length == 0) {			throw new NullPointerException("评论ID不能为空！");		}		for (int i = 0; i < ids.length; i++) {			if(StringUtils.isBlank(ids[i])){				throw new NullPointerException("评论ID不能为空！");			}						Comment c = new Comment();			c.setId(ids[i]);			c.setStatus(status);			dao.updateStatus(c);		}			}	@Override	public int selectNotReplyCount() {		return dao.selectNotReplyCount();	}}