package com.jiagouedu.services.manage.commenttype.impl;import com.jiagouedu.core.ServersManager;import com.jiagouedu.services.manage.commenttype.CommentTypeService;import com.jiagouedu.services.manage.commenttype.bean.CommentType;import com.jiagouedu.services.manage.commenttype.dao.CommentTypeDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("commentTypeServiceManage")public class CommentTypeServiceImpl extends ServersManager<CommentType, CommentTypeDao>		implements CommentTypeService {    @Resource(name = "commentTypeDaoManage")    @Override    public void setDao(CommentTypeDao commentTypeDao) {        this.dao = commentTypeDao;    }    @Override	public int update(CommentType e) {		dao.updateAllN();		super.update(e);		return 1;	}}