package com.jiagouedu.web.action.manage.accountrank;import com.jiagouedu.core.ManageContainer;import com.jiagouedu.core.dao.page.PagerModel;import com.jiagouedu.core.exception.NotThisMethod;import com.jiagouedu.services.manage.accountrank.AccountRankService;import com.jiagouedu.services.manage.accountrank.bean.AccountRank;import com.jiagouedu.web.action.BaseController;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import javax.servlet.http.HttpServletRequest;/** * 会员等级 * @author jqsl2012@163.com * @author wukong 图灵学院 QQ:245553999 * */@Controller@RequestMapping("/manage/accountRank/")public class AccountRankAction extends BaseController<AccountRank> {	private static final long serialVersionUID = 1L;    @Autowired	private AccountRankService accountRankService;    private static final String page_toList = "/manage/accountRank/accountRankList";    private static final String page_toEdit = "/manage/accountRank/accountRankEdit";    private static final String page_toAdd = "/manage/accountRank/accountRankEdit";    private AccountRankAction(){        super.page_toList = page_toList;        super.page_toAdd = page_toAdd;        super.page_toEdit = page_toEdit;    }    @Override	public AccountRankService getService() {		return accountRankService;	}    @Override	protected void selectListAfter(PagerModel pager) {		pager.setPagerUrl("selectList");	}	public void setAccountRankService(AccountRankService accountRankService) {		this.accountRankService = accountRankService;	}    @Override	public void insertAfter(AccountRank e) {		e.clear();	}	/**	 * 不支持此方法	 */	@Override	public String insert(HttpServletRequest request, @ModelAttribute("e") AccountRank e, RedirectAttributes flushAttrs) throws Exception {		throw new NotThisMethod(ManageContainer.not_this_method);	}	/**	 * 不支持此方法	 */	@Override	public String deletes(HttpServletRequest request, String[] ids, @ModelAttribute("e") AccountRank e, RedirectAttributes flushAttrs) throws Exception {		throw new NotThisMethod(ManageContainer.not_this_method);	}}