package com.jiagouedu.web.action.manage.navigation;import com.jiagouedu.core.dao.page.PagerModel;import com.jiagouedu.services.manage.navigation.NavigationService;import com.jiagouedu.services.manage.navigation.bean.Navigation;import com.jiagouedu.web.action.BaseController;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import javax.servlet.http.HttpServletRequest;/** * 友情链接管理 * @author wukong 图灵学院 QQ:245553999 */@Controller@RequestMapping("/manage/navigation/")public class NavigationAction extends BaseController<Navigation> {	private static final long serialVersionUID = 1L;	@Autowired	private NavigationService navigationService;	private static final String page_toList = "/manage/navigation/navigationList";	private static final String page_toEdit = "/manage/navigation/navigationEdit";	private static final String page_toAdd = "/manage/navigation/navigationEdit";	private NavigationAction() {		super.page_toList = page_toList;		super.page_toAdd = page_toAdd;		super.page_toEdit = page_toEdit;	}	@Override	public NavigationService getService() {		return navigationService;	}	@Override	protected void selectListAfter(PagerModel pager) {		pager.setPagerUrl("selectList");	}	public void setNavigationService(NavigationService navigationService) {		this.navigationService = navigationService;	}	@Override	public void insertAfter(Navigation e) {		e.clear();	}	@RequestMapping(value = "insert", method = RequestMethod.POST)	@Override	public String insert(HttpServletRequest request, Navigation e, RedirectAttributes flushAttrs) throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.insert(request, e, flushAttrs);	}	@RequestMapping(value = "update", method = RequestMethod.POST)	@Override	public String update(HttpServletRequest request, Navigation e, RedirectAttributes flushAttrs) throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.update(request, e, flushAttrs);	}}