package com.jiagouedu.services.front.emailnotifyproduct.impl;import com.jiagouedu.core.ServersManager;import com.jiagouedu.services.front.emailnotifyproduct.EmailNotifyProductService;import com.jiagouedu.services.front.emailnotifyproduct.bean.EmailNotifyProduct;import com.jiagouedu.services.front.emailnotifyproduct.dao.EmailNotifyProductDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("emailNotifyProductServiceFront")public class EmailNotifyProductServiceImpl extends        ServersManager<EmailNotifyProduct, EmailNotifyProductDao> implements EmailNotifyProductService {    @Resource(name = "emailNotifyProductDaoFront")    @Override    public void setDao(EmailNotifyProductDao emailNotifyProductDao) {        this.dao = emailNotifyProductDao;    }    @Override	public int selectCount(EmailNotifyProduct ep) {		return dao.selectCount(ep);	}}